css:
/* Allgemein */
.print-format {
    /*margin - top: 30mm;*/
    margin-left: 10mm;
    font-size: 11pt;
}

/* Deckblatt */
#tabelle_deckblatt {
	font-size: 15pt;
}

#deckblatt {
	font-size: 13pt;
}

#deckblatt h3 {
    margin-bottom: 0px !important;
    margin-top: 10px !important;
}

#deckblatt ul {
    margin-bottom: 0px !important;
    line-height: 120% !important;
}

#deckblatt .page-break {
    padding: 0 0 0 0 !important;
}

#tabelle_deckblatt {
    width: 100%;
    padding: 10px 0 10px 0 !important;
}

#tabelle_deckblatt tr {
    padding: 10px 0 10px 0 !important;
}

#tabelle_deckblatt td {
    padding: 10px 0 10px 0 !important;
}

#tabelle_deckblatt_td1 {
    width: 30%;
}

/* Content */
/* Ausgangslage */
#tabelle_ausgangslage {
    width: 100%;
}

#tabelle_ausgangslage th {
    background-color: white !important;
    font-weight: bold !important;
    padding: 1px 1px 1px 1px !important;
    border: 1px solid black !important;
}

#tabelle_ausgangslage td {
    padding: 1px 1px 1px 1px !important;
    border: 1px solid black !important;
}

/* #tabelle_ausgangslage tr td div {
    page-break-inside: avoid;
} */

#ausgangslage_th_1 {
    width: 10%;
}

#ausgangslage_th_2 {
    width: 25%;
    border-collapse: separate !important;
}


/* Korrespondenz */
#tabelle_korrespondenz {
    width: 100%;
}

/* #tabelle_korrespondenz tr td div {
    page-break-inside: avoid;
} */

#tabelle_korrespondenz th {
    background-color: white !important;
    font-weight: bold !important;
    padding: 1px 1px 1px 1px !important;
    border: 1px solid black !important;
}

#tabelle_korrespondenz td {
    padding: 1px 1px 1px 1px !important;
    border: 1px solid black !important;
}

#tabelle_korrespondenz_th_1 {
    width: 10%;
}

#tabelle_korrespondenz_th_2 {
    width: 25%;
    border-collapse: separate !important;
}

/* Footer */
#footer-html p {
    font-size: 8pt !important;
	margin-top: 0px !important;
}
