css:
/* Allgemein */
.print-format {
    margin-left: 15mm;
    margin-right: 10mm;
    font-size: 11pt;
}

/* Adress Section */
#address_section {
    width: 100%;
    padding: 0 0 0 0 !important;
    margin-bottom: 10px;
}

#address_section tr {
    padding: 0 0 0 0 !important;
}

#address_section td {}

#address_section_td_1 {
    width: 58.5%;
    font-size: 9pt !important;
    padding: 0 0 0 0 !important;
    line-height: 120%;
}

#address_section_td_2 {
    /*font-size: 10pt !important;*/
    padding: 70px 0 0 0 !important;
    line-height: 120%;
}

#address_section_datumzeile {
    margin-top: 60px;
}

/* content */
#dokumenteninhalt {
    padding: 0 !important;
}

#dokumenteninhalt p {
    line-height: 120%;
}

#dokumenteninhalt ol {
    line-height: 120%;
}

#dokumenteninhalt li {
    padding-bottom: 10px;
}

#kontaktdaten {
    width: 100%;
    padding: 0 !important;
}

#kontaktdaten tr {
    border-bottom: 0.5px solid black;
    padding: 0 !important;
}

#kontaktdaten td {
    padding: 2px 0 2px 0 !important;
}

#zusatz_zeile_1 {
	padding-bottom: 5px;
}

#titelzeile {
	margin-bottom: 5px;
}

/* Footer */
#footer-html p {
    font-size: 8pt !important;
}
